MkSQL.sql:
SELECT user_SEQ, name, address
FROM User
WHERE name LIKE @user_name@;

SELECT user_SEQ, name, address
FROM User
WHERE name LIKE @user_name@ and address LIKE @user_info@;

UPDATE User
SET name = @user_name@, address = @user_address@
WHERE user_SEQ = @user_SEQ@;

SELECT user_SEQ, name, address
FROM User
@CONDITION@;
